import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r,o,c as s,d as i,e as t,b as g,f as n}from"./app-13fcc9dd.js";const l={},c=n('<h2 id="_1-开始联机前-设置好你的机签名-profile-、键位、卡组" tabindex="-1"><a class="header-anchor" href="#_1-开始联机前-设置好你的机签名-profile-、键位、卡组" aria-hidden="true">#</a> <strong>1.开始联机前，设置好你的机签名（Profile）、键位、卡组</strong></h2><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>在练习模式选人界面按C打开菜单</strong><br><strong>先选择好机签，右上角变化后，再去设置1P卡组和按键</strong></p><p>●机签名字最好是可以方便在QQ等地方找到你的名字，不要设置低俗无礼或者无意义不好打出来的名字<br> ●检查主菜单Config内，打开了FPS显示，这很重要。推荐录像保存设置为全保存。</p></div><figure><img src="https://img.514.live/img/202308011408888.png" alt="修改机签名，直接去profile文件夹里把文件改名即可" width="640" height="480" tabindex="0" loading="lazy"><figcaption>修改机签名，直接去profile文件夹里把文件改名即可</figcaption></figure><h2 id="_2-检测autopunch" tabindex="-1"><a class="header-anchor" href="#_2-检测autopunch" aria-hidden="true">#</a> <strong>2.检测AutoPunch</strong></h2><p>在使用大厅联机之前，你必须先检测你的网络环境支不支持AP联机，若不支持，则不能通过大厅联机，可以暂时把大厅mod关掉或者按S恢复传统菜单（后续可能会加入大厅支持swarm）</p><figure><img src="https://img.514.live/img/202308011508215.png" alt="运行“autopunch检测”" tabindex="0" loading="lazy"><figcaption>运行“autopunch检测”</figcaption></figure><p><img src="https://img.514.live/img/202308011509662.png" alt="支持" width="320" height="200" loading="lazy">👈支持 不支持👉<img src="https://img.514.live/img/202308011712361.png" alt="不支持" width="320" height="200" loading="lazy"></p>',7),h={class:"hint-container info"},p=i("p",{class:"hint-container-title"},"相关信息",-1),d=i("p",null,[t("显示 "),i("strong",null,"Hole-punching is supported"),t(" 即为支持可用；"),i("br"),t(" 而 "),i("strong",null,"NOT supported"),t(" 即为不支持，不能在大厅内联机，请使用swarm和原版菜单。")],-1),f=i("strong",null,"【UDP 打洞 (UDP Hole Punching) 原理 - CSDN】",-1),u=i("br",null,null,-1),_={href:"https://blog.csdn.net/jsict/article/details/122828898",target:"_blank",rel:"noopener noreferrer"},m=n('<figure><img src="https://img.514.live/img/202308011510073.png" alt="如果遇到类似这样的情况，重新多试几次，直到显示出结果" tabindex="0" loading="lazy"><figcaption>如果遇到类似这样的情况，重新多试几次，直到显示出结果</figcaption></figure><h2 id="_3-进入-vs-network-菜单" tabindex="-1"><a class="header-anchor" href="#_3-进入-vs-network-菜单" aria-hidden="true">#</a> <strong>3.进入 VS NETWORK 菜单</strong></h2><figure><img src="https://img.514.live/img/202308011410282.png" alt="按S切换菜单" width="640" height="480" tabindex="0" loading="lazy"><figcaption>按S切换菜单</figcaption></figure><figure><img src="https://img.514.live/img/202308011410870.png" alt="" width="640" height="480" tabindex="0" loading="lazy"><figcaption></figcaption></figure><div class="hint-container info"><p class="hint-container-title">相关信息</p><p>●若是自己建主，向群里发IP，最好附带相关信息，如：<br> 42.193.7.216:58309 对4 妖梦 （不带信息，谁都能进）<br> 42.193.7.216:58309 对5 UU，等到12点/打十局/抢十 （注意别人已经说好了打多久）<br> 42.193.7.216:58309 新人对三灵梦，希望水平差不多的其他新人 （意思老手不要进来）<br> 42.193.7.216:58309 3+文，希望水平高点的指教 （意思新手不要进来）<br> 42.193.7.216:58309 @xxx （意思是有约了，别的人不要进来）<br> 42.193.7.216:58309 对五 黑白 妖梦，随时切 （意思是可能随时会中断游戏）</p></div><h2 id="_4-成功进入对战-选人界面" tabindex="-1"><a class="header-anchor" href="#_4-成功进入对战-选人界面" aria-hidden="true">#</a> <strong>4.成功进入对战，选人界面</strong></h2><div class="hint-container info"><p class="hint-container-title">相关信息</p><p>●进入后，请在QQ群内回复别人，让别人知道是在和谁玩，便于后续交流<br><strong>●可在选人界面按C，能且只能修改卡组</strong><br> ●若不想继续打了，可以在群里at对手，或者在选人界面连续按X发出框框的声音，表示Last，最后一局<br> ●如果发现对手一直没反应，最好查看下QQ群消息和大厅聊天框，也许他正在那边打字</p></div><figure><img src="https://img.514.live/img/202308011415274.png" alt="选人界面" width="640" height="480" tabindex="0" loading="lazy"><figcaption>选人界面</figcaption></figure><h2 id="_5-进入对局内" tabindex="-1"><a class="header-anchor" href="#_5-进入对局内" aria-hidden="true">#</a> <strong>5.进入对局内</strong></h2><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>1P方请根据正下方延迟使用大键盘加减号调整左下角delay值，简单规则就是越低越好，只要不发生令人不适的严重回滚即可，若回滚现象严重则增大延迟</strong></p><p>●如果发现对手一直没反应，最好查看下QQ群消息和大厅聊天框，也许他正在那边打字<br> ●如果发现自己和对手实力悬殊（太强或者太新），应该中途在QQ群或聊天框内询问，两人实力差距太大，还要不要继续游戏（我个人认为是没必要）<br> ●最好不要无端故意中途ESC结束对局，会导致无法保存Rep，若有紧急情况要离开可以在事后QQ群或者聊天框通知对手<br> ●打完结束后请回复谢指教，谢玩之类的话感谢对战</p></div><figure><img src="https://img.514.live/img/202308011417441.png" alt="如果没有数字显示，去主菜单Config里打开FPS显示" tabindex="0" loading="lazy"><figcaption>如果没有数字显示，去主菜单Config里打开FPS显示</figcaption></figure>',11);function b(v,x){const e=r("ExternalLinkIcon");return o(),s("div",null,[c,i("div",h,[p,d,i("p",null,[f,u,i("a",_,[t("https://blog.csdn.net/jsict/article/details/122828898"),g(e)])])]),m])}const Q=a(l,[["render",b],["__file","BeforePlaying.html.vue"]]);export{Q as default};
