import{_ as r}from"./plugin-vue_export-helper-c27b6911.js";import{r as i,o as e,c as l,a as t,d as s,b as n,f as g}from"./app-aad1fb6d.js";const a={},c=t("h2",{id:"practiceex",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#practiceex","aria-hidden":"true"},"#"),n(" PracticeEX")],-1),p={class:"hint-container tip"},d=t("p",{class:"hint-container-title"},"提示",-1),h=t("p",null,"2023-12-8 已更新 PracticeEX 高级练习模式汉化版",-1),u={href:"https://www.bilibili.com/video/BV1894y177s6/",target:"_blank",rel:"noopener noreferrer"},_=t("strong",null,"【非想天则 Mod】发布 Practice-EX 高级练习模式汉化版",-1),F=t("br",null,null,-1),b={href:"https://www.bilibili.com/video/BV1894y177s6/",target:"_blank",rel:"noopener noreferrer"},f=t("div",{class:"hint-container warning"},[t("p",{class:"hint-container-title"},"BUG 注意"),t("p",null,[t("strong",null,"注意在录制木桩动作操作2P的时候，不要按F7"),t("br"),t("strong",null,"F5、F6、F7在原本练习模式中就是用来给手柄2P录制重播动作的，F5开始录制 F6结束录制 F7播放动作，所以在用插件录制时按F7会导致无法移动，此时按F5或F6即可恢复")]),t("p",null,[t("strong",null,"【不要设置随机空中受身 Random Airtech，会让游戏崩溃】")])],-1),v=t("h3",{id:"木桩动作录制技巧",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#木桩动作录制技巧","aria-hidden":"true"},"#"),n(" 木桩动作录制技巧")],-1),m={class:"hint-container tip"},B=t("p",{class:"hint-container-title"},"让木桩最速执行动作，如防御后用卡，或者执行宏（macro，录制些 623 或者 236 什么的）",-1),w=t("p",null,[n("自带的那个 AFTER BLOCK（防御后执行动作）会有点问题，做用卡和执行宏会慢 1F ；"),t("br"),n(" 此时我们想要最速，可以使用防御时刷键的方法：")],-1),P=t("p",null,[t("strong",null,"就是切换到 ON BLOCK（防御时执行动作），并搭配木桩延迟，可以实现模拟防御后刷键预输入的情景，通过预输入我们就可以让木桩最速了")],-1),y={href:"https://www.bilibili.com/video/BV11p4y177Ng/",target:"_blank",rel:"noopener noreferrer"},O=t("strong",null,"配套教程视频 传送门： 【非想天则】新则 - 练习模式 EX - 让木桩最速出招的录制技巧",-1),x=g('<div class="hint-container info"><p class="hint-container-title">情况一：最速 5A、用卡等（只需要一帧输入）</p><p>操作方法举例—————录制 5A 或者 用卡：</p><ol><li>按 <strong>录制热键（默认是 HOME）</strong> 切换到操控木桩模式，此时右上角显示<strong>黄条</strong></li><li>按 F11 暂停，然后按 <strong>录制热键（默认是 HOME）</strong>，然后右上角显示<strong>红条</strong></li><li><strong>按住</strong> A 或者 用卡键，然后在此基础上，按 F12 下一帧</li><li>按 F11 取消暂停，不要按键，差不多走到 十几F 的时候就可以再按一次 <strong>录制热键（默认是 HOME）</strong>，就是结束并保存录制了</li><li>按 <strong>播放热键</strong> 让它播放一次，一般是没问题的</li><li>在 <strong>Triggers 菜单</strong>里，选择 <strong>ON BLOCK -&gt; MACRO</strong>，<strong>Delay 设置为 0F</strong></li></ol></div><div class="hint-container info"><p class="hint-container-title">情况二：最速 DA 或 623 或 236 等（需要三帧预输入）</p><p>操作方法举例—————录制 623B：</p><ol><li>按 <strong>录制热键（默认是 HOME）（默认是 HOME）</strong> 切换到操控木桩模式，此时右上角显示<strong>黄条</strong></li><li>按 F11 暂停，然后按 <strong>录制热键（默认是 HOME）</strong>，然后右上角显示<strong>红条</strong></li><li><strong>按住</strong> 6，然后在此基础上，按 F12 下一帧</li><li><strong>按住</strong> 2，然后在此基础上，按 F12 下一帧</li><li><strong>按住</strong> 3 和 B，然后在此基础上，按 F12 下一帧<br><strong>（或者3 和 B 分开，这样需要录四帧，下面的 Delay 值也要调到 4F）</strong></li><li>按 F11 取消暂停，不要按键，差不多走到 十几F 的时候就可以再按一次 <strong>录制热键（默认是 HOME）</strong>，就是结束并保存录制了</li><li>按 <strong>播放热键</strong> 让它播放一次，一般是没问题的</li><li>在 <strong>Triggers 菜单</strong>里，选择 <strong>ON BLOCK -&gt; MACRO</strong>，<strong>Delay 设置为 3F</strong></li></ol></div><h2 id="labtool" tabindex="-1"><a class="header-anchor" href="#labtool" aria-hidden="true">#</a> Labtool</h2><figure><img src="https://bu.dusays.com/2023/08/29/64edf489df703.png" alt="" width="640" tabindex="0" loading="lazy"><figcaption></figcaption></figure><div class="hint-container info"><p class="hint-container-title">相关信息</p><p>注意这些热键都是<strong>大键盘</strong>上的数字</p><p>按1可恢复双方位置【<strong>按住1P的方向键再按1可以移动至默认预设好的五等份点位置</strong>】<br> （小心角色被某些招式和击飞或演出时复位会出现角色卡住的情况）</p><p>按2可记录双方当前位置，练习连段和微调位置(逐帧走路卡距离)等用非常方便！【<strong>最好不要在角色非静止时保存位置</strong>】</p><p>按4可将 1P 的必杀恢复为初始默认状态</p><p>按5可启用状态颜色显示（可被康状态红色，擦弹状态透明，硬直状态紫色，无敌状态蓝色）</p><p><strong>信息举例：</strong><br><strong>Gap：0F</strong> 表示 1P的两次攻击之间有 1F 缝，非无缝 （以0为1，以此类推）<br><strong>P1 is +5F</strong> 表示 1P有利 5F，最速出手7F招中间有2F缝<br><strong>P1 is -6F</strong> 表示 1P不利 6F，可被6F的招确反<br><strong>P1 is [+24F]</strong> 表示 1P大跳取消后有利 24F</p><p><strong>没显示Gap说明是无缝或是缝太大</strong></p></div><div class="hint-container warning"><p class="hint-container-title">注意</p><p><strong>注意不要点窗口右上角的X关掉这个LabTool的程序框，他是和游戏程序共生的</strong><br><strong>你点了X游戏也会被关掉，你不想看它可以点最小化</strong></p><p><strong>若有时候切回游戏发现莫名卡住操作不了，很有可能是因为 LabTool 的控制台窗口的属性里开启了快速编辑模式，然后你选中了窗口出现光标闪烁会导致窗口不能刷新显示新的文字，游戏就会卡住等待</strong></p><p><strong>解决办法：右键取消光标选中，或者在标题栏右键-属性-关闭快速编辑模式即可恢复正常</strong></p></div><h2 id="replayinputview" tabindex="-1"><a class="header-anchor" href="#replayinputview" aria-hidden="true">#</a> ReplayInputView</h2><div class="hint-container info"><p class="hint-container-title">相关信息</p><p><strong>单机和观战下才能使用，网战中不能使用</strong></p><p><strong>F4</strong>显示<strong>判定框</strong>以及受身时间条</p><p><strong>F6</strong>显示帧数、位置、速度等<strong>信息</strong> （不要在观战下打开，与giuroll之间有bug会使游戏崩溃）</p><p><strong>F7</strong>显示<strong>按键输入</strong>（继续按F7来调整布局；观战下按键识别有点小瑕疵，需要花点耐心调整）</p><p><strong>F11</strong>暂停，<strong>F12</strong>下一帧（<strong>逐帧操作</strong>） （不要在观战时按）</p><p><strong>绿框是受击判定</strong>，绿框变蓝色代表此时<strong>可被康</strong>，绿框变空心代表此时有体术无敌状态（不一定是完全无敌，也不是所有无敌状况都会空心显示）</p><p><strong>红框是攻击判定</strong>（某些招数或者弹幕会有判定残留显示或者假判定）</p><p><strong>黄框是占位判定</strong>（相当于投技的受击判定）</p></div><div class="hint-container tip"><p class="hint-container-title">如何逐帧输入指令</p><p><strong>在暂停状态下，按住某个键再按F12即可逐帧输入按键（注意三键冲突，可能会导致按不下去F12）</strong></p><p><strong>例如逐帧输入623B时：</strong><br><strong>可先按住6后下一帧，接着按住2后下一帧，随后按住3以及B后下一帧，若冲突键位了可按住3下一帧再按B下一帧</strong></p><p><strong>逐帧输入22B时：</strong><br><strong>先按住2后下一帧，什么都不按后下一帧，再按住2和B后下一帧</strong></p></div><figure><img src="https://bu.dusays.com/2023/08/29/64ee0be96fa82.png" alt="F6 数据含义" width="800" tabindex="0" loading="lazy"><figcaption>F6 数据含义</figcaption></figure><figure><img src="https://bu.dusays.com/2023/10/30/653e91c4c5846.png" alt="F6 数据含义-英文" width="640" tabindex="0" loading="lazy"><figcaption>F6 数据含义-英文</figcaption></figure><div class="hint-container info"><p class="hint-container-title">关于1hst&quot;和&quot;2hst&quot;这个值</p><p>hst 是 hit state 的 缩写，暂时只知道触发和变化的规律，并不知道有什么实际意义</p><p>&quot;1hst&quot;和&quot;2hst&quot;值<strong>变化规律</strong>：</p><ul><li>游戏初始状态为“0”；</li><li>变成“0”的条件：开始出招；体术挥空；进入防御（但是破防和挨打不变）；</li><li>变成“1”的条件：体术命中；成功丢出弹幕；青蛙成功遁地；</li><li>变成“2”的条件：对手防御了自己的体术；</li><li>一些例外情况，尤其是SC，如：青蛙四卡蛙狩，启动时会置0，但是后续无论如何也不会变成1或2；</li></ul><p>青蛙L姿水蛙神不能取消的Bug：<br> 当&quot;1hst&quot;或&quot;2hst&quot;值为“0”的时候，1P或2P青蛙L姿（荷叶）214改水蛙神炸弹就不能跳跃取消或者出招取消，为“1”或“2”时可正常取消</p></div>',12);function E(M,q){const o=i("ExternalLinkIcon");return e(),l("div",null,[c,t("div",p,[d,h,t("p",null,[t("a",u,[_,s(o)]),F,t("strong",null,[t("a",b,[n("https://www.bilibili.com/video/BV1894y177s6/"),s(o)])])])]),f,v,t("div",m,[B,w,P,t("p",null,[t("a",y,[O,s(o)])])]),x])}const A=r(a,[["render",E],["__file","PracticeMods.html.vue"]]);export{A as default};
